close; clear; home;fmax = 1.0; %GHzdf = 0.0001; %GHzfmin = 0.0001; %GHzdt = 1/(2.0*fmax); %nstmax = 1/df; %nsE_C = 1.0e2; %TeVt = -tmax/2:dt:tmax/2;f = fmin:df:fmax; %GHz[E,t] = ARZ_Efield(t,E_C);[~,j] = max(E);offset = 0;offset2 = 0;E = circshift(E,[-j+offset,0]);E_f = fft(E);E_f = E_f(1:length(t)/2);E_p = unwrap(arg(E_f))*180/pi-offset2;%Scalingf = f*1000.0; %MHzsemilogx(f,E_p,'-o');axis([1 1e3 0 180])S_obj = [f' E_p];save 'arvz_phase.dat' S_obj